mapper.select:
select

from PMS_APP_TRANS_INFO
where ID = #{id,jdbcType=DECIMAL}

select

from PMS_APP_TRANS_INFO t where t.ORDERID =
#{orderId,jdbcType=VARCHAR}

update
PMS_APP_TRANS_INFO t set t.RESPONSESTATE =
#{responseStatus,jdbcType=VARCHAR}
where t.ORDERID =
#{orderid,jdbcType=VARCHAR}

update PMS_APP_TRANS_INFO t set t.RESPONSESTATE =
#{responseStatus,jdbcType=VARCHAR},t.SERIALNO =
#{serialNo,jdbcType=VARCHAR},
t.SEARCHNUM =
#{searchNum,jdbcType=VARCHAR}


	,t.TRADETIME = #{tradeTime,jdbcType=VARCHAR}


where t.ORDERID = #{orderid,jdbcType=VARCHAR}

select

from PMS_APP_TRANS_INFO t where t.MERCID = #{mercId}

	and t.tradetime between #{startTime} and #{endTime}


	AND PAYMENTTYPE = #{paymentType,jdbcType=VARCHAR}


	AND TRADETYPECODE = #{tradeType,jdbcType=VARCHAR}

order by t.id desc

select ACCOUNTINGFLAG , STATUS from PMS_APP_TRANS_INFO
where ORDERID =
#{orderid,jdbcType=VARCHAR}

select * from (select * from (select substr(t.tradetime, 0, 10) as timestr, sum(t.factamount) as amountsum, sum(t.poundage) as poundagesum from pms_app_trans_info t where t.status = '0' and t.tradetypecode != '7'
and t.mercid = #{mercid,jdbcType=VARCHAR}
and t.paymentcode= #{paymentcode,jdbcType=VARCHAR}
and t.tradetypecode= #{tradetypecode,jdbcType=VARCHAR}
group by substr(t.tradetime, 0, 10)) order by timestr desc) where rownum <= #{dates,jdbcType=INTEGER}

SELECT * FROM (SELECT A.*, ROWNUM RN FROM ( select * from (select * from PMS_APP_TRANS_INFO t where t.tradetypecode != '7'
and t.mercid = #{mercid,jdbcType=VARCHAR}
and t.paymentcode= #{paymentcode,jdbcType=VARCHAR}
and t.tradetypecode= #{tradetypecode,jdbcType=VARCHAR}
and substr(tradetime,0,10) between #{beginDate,jdbcType=VARCHAR} and #{endDate,jdbcType=VARCHAR} order by t.tradetime desc) )A WHERE ROWNUM <= 1* (#{pageNum}*#{pageSize})) WHERE RN >= (#{pageNum}-1)*#{pageSize}+1

SELECT count(*) FROM PMS_APP_TRANS_INFO t
WHERE
t.tradetypecode != '7'

	and t.mercid = #{mercid,jdbcType=VARCHAR}


	and t.paymentcode= #{paymentcode,jdbcType=VARCHAR}


	and t.tradetypecode= #{tradetypecode,jdbcType=VARCHAR}


and substr(t.tradetime,0,10) between #{beginDate,jdbcType=VARCHAR} and
#{endDate,jdbcType=VARCHAR}

SELECT * FROM (SELECT A.*, ROWNUM RN FROM ( select * from (select * from (select * from (select substr(t.finishtime, 0, 10) as timestr,substr(t.tradetime, 0, 10) as timestrtrade, sum(t.factamount) as amountsum, sum(t.poundage) as poundagesum from pms_app_trans_info t where t.status = '0' and t.tradetypecode = '7' and t.mercid = #{mercid,jdbcType=VARCHAR}
and substr(t.finishtime, 0, 7) between #{beginMonth,jdbcType=VARCHAR} and #{endMonth,jdbcType=VARCHAR}
group by substr(t.finishtime, 0, 10), substr(t.tradetime, 0, 10)) order by timestr desc ) ) )A WHERE ROWNUM <= 1* (#{pageNum}*#{pageSize})) WHERE RN >= (#{pageNum}-1)*#{pageSize}+1

select count(*) from (select * from (select substr(t.finishtime, 0, 10) as timestr,substr(t.tradetime, 0, 10) as timestrtrade, sum(t.factamount) as amountsum, sum(t.poundage) as poundagesum from pms_app_trans_info t where t.status = '0' and t.tradetypecode='7'
and t.mercid = #{mercid,jdbcType=VARCHAR}
and t.paymentcode= #{paymentcode,jdbcType=VARCHAR}
and substr(t.finishtime, 0, 7) between #{beginMonth,jdbcType=VARCHAR} and #{endMonth,jdbcType=VARCHAR}
group by substr(t.finishtime, 0, 10), substr(t.tradetime, 0, 10)) order by timestr desc)

select * from (select * from (select substr(t.tradetime, 0, 10) as timestr, sum(t.factamount) as amountsum, sum(t.poundage) as poundagesum from pms_app_trans_info t where t.tradetypecode != '7'
and t.mercid = #{mercid,jdbcType=VARCHAR}
and t.paymentcode= #{paymentcode,jdbcType=VARCHAR}
and t.tradetypecode= #{tradetypecode,jdbcType=VARCHAR}
group by substr(t.tradetime, 0, 10)) order by timestr desc) where rownum <= #{dates,jdbcType=INTEGER}

